        bottom: 'June 6, 2019    By Rickie Baroch'
    },
    {
        img: '../../img/img1.png',
        title: 'Tourism',
        content: 'One of Saturn’s largest rings may be newer than anyone',
        bottom: 'June 6, 2019    By Rickie Baroch'
    }
]

const Item = (arr) => {
    let result = arr.map(function(value) {
        return `<div className="img-item-header">
        <img src=\"${value.img}\" alt="" className=\"img-item\" />
        </div>
        <span>${value.title}</span>`
    })
    return result.join("")
}
const Hehe = Item(items);
export default Hehe
